/**
 * 01. Page title.
 *
 * Apply on "Text Editor" module
 * containing a H1 title.
 */

	.envato-kit-7-title-page h1 {
		font-weight: 400;
		text-transform: uppercase;
		letter-spacing: .15em;
	}

	@media (min-width: 60em) {
		.envato-kit-7-title-page h1 {
			font-size: 3.329em;
		}
	}

	@media (min-width: 80em) {
		.envato-kit-7-title-page h1 {
			font-size: 4.236em;
		}
	}



/**
 * 02. Hero text.
 *
 * Apply on "Text Editor" module
 * containing a H2, H3 title
 * or a paragraph.
 */

	.envato-kit-7-text-hero h2,
	.envato-kit-7-text-hero h3,
	.envato-kit-7-text-hero p {
		font-size: 2.058em;
		font-weight: 400;
		text-transform: uppercase;
		letter-spacing: .15em;
	}

	@media (min-width: 60em) {
		.envato-kit-7-text-hero h2,
		.envato-kit-7-text-hero h3,
		.envato-kit-7-text-hero p {
			font-size: 2.618em;
		}
	}

	@media (min-width: 80em) {
		.envato-kit-7-text-hero h2,
		.envato-kit-7-text-hero h3,
		.envato-kit-7-text-hero p {
			font-size: 3.329em;
		}
	}



/**
 * 03. Button.
 *
 * Apply on "Text Editor" or "HTML"
 * module containing a link (anchor)
 * or button HTML tag.
 */

	.envato-kit-7-button a,
	.envato-kit-7-button button {
		position: relative;
		display: inline-block;
		padding: 1em;
		line-height: 1.19;
		font-weight: 700;
		text-decoration: none;
		text-transform: uppercase;
		letter-spacing: .1em;
		background: transparent;
		color: inherit;
		border: 3px solid;
		border-radius: 4px;
		cursor: pointer;
	}

	.envato-kit-7-button a::before,
	.envato-kit-7-button button::before {
		content: '';
		position: absolute;
		width: 100%;
		height: 100%;
		left: 0;
		top: 0;
		background: currentColor;
		opacity: .07;
		transform-origin: 0 50%;
		transform: scaleX(0);
		transition: transform .3s;
	}

	.envato-kit-7-button a:hover::before,
	.envato-kit-7-button button:hover::before {
		transform: none;
	}



/**
 * 04. Text alignment.
 *
 * Styles force left/center/right
 * text alignment on medium and
 * large devices only.
 * Small devices text alignment
 * falls back to default according
 * to your language setup.
 *
 * Apply on any row, column or module.
 *
 * You may need to tweak the `769px`
 * below to match the value of small
 * device breakpoint from your Beaver
 * Builder global settings (default
 * is 768px, so we set the value of
 * 768 + 1 = 769px).
 */

	.envato-kit-7-text-align-center {
		text-align: center !important;
	}

	@media (min-width: 769px) {
		.envato-kit-7-text-align-left {
			text-align: left !important;
		}

		.envato-kit-7-text-align-right {
			text-align: right !important;
		}
	}



/**
 * 05. Full height row columns.
 *
 * Apply on full-height row.
 * Immediate column children will
 * also get stretched to full
 * screen height.
 */

	.envato-kit-7-full-height-columns > .fl-row-content-wrap > .fl-row-content > .fl-col-group:only-child {
		min-height: 100vh;
	}



/**
 * 06. Others.
 */

	/**
	 * Column and module negative
	 * margins overlap.
	 *
	 * Applies automatically on all
	 * modules.
	 */

	.fl-col-content,
	.fl-module-content {
		position: relative;
	}

	.fl-module-content {
		z-index: 1;
	}

	/**
	 * Mobile column width.
	 *
	 * Applies automatically on all
	 * columns. You may only need to
	 * tweak the `768px` below to
	 * match the value of small
	 * device breakpoint from your
	 * Beaver Builder global settings.
	 */

	@media (max-width: 768px) {
		.fl-col-small {
			max-width: 100%;
		}
	}


